mapper.select:
SELECT DEBATE_ID, MEETING_TYPE, DEBATE_TYPE, TITLE, DESCRIPTION, DEBATE_START_DATE, DEBATE_END_DATE, DEBATE_BOOK_ID, ISBN13, COVER FROM(SELECT ROWNUM AS RNUM,DEBATE_ID, MEETING_TYPE, DEBATE_TYPE, TITLE, DESCRIPTION, DEBATE_START_DATE, DEBATE_END_DATE, DEBATE_BOOK_ID, ISBN13, COVER FROM(SELECT I.DEBATE_ID,T.MEETING_TYPE,P.DEBATE_TYPE, I.TITLE,B.DESCRIPTION, TO_CHAR(I.DEBATE_START_DATE,'YYYY-MM-DD')AS DEBATE_START_DATE, TO_CHAR(I.DEBATE_END_DATE,'YYYY-MM-DD')AS DEBATE_END_DATE, DB.DEBATE_BOOK_ID,DB.ISBN13,B.COVER FROM TBL_DEBATE I JOIN TBL_MEETING_TYPE T ON I.MEETING_ID = T.MEETING_ID JOIN TBL_DEBATE_TYPE P ON I.DEBATE_TYPE_ID = P.DEBATE_TYPE_ID JOIN TBL_DEBATE_BOOK DB ON I.DEBATE_ID = DB.DEBATE_ID JOIN TBL_BOOK B ON DB.ISBN13 = B.ISBN13 WHERE MEETING_TYPE='온라인' OR MEETING_TYPE='오프라인' ORDER BY DEBATE_ID DESC )
WHERE ROWNUM <= #{page} * #{amount}
) WHERE RNUM > (#{page} - 1) * #{amount}
